import React from "react";

export default function Pagenation({
  postsPerPage,
  totalRes,
  setCurrentPage,
  isHidden,
  styles,
}) {
  let pages = [];
  for (let i = 1; i <= Math.ceil(totalRes / postsPerPage); i++) {
    pages.push(i);
  }
  return (
    <div className={`${isHidden} ${styles} `}>
      {" "}
      {pages.map((page, index) => {
        return (
          <button key={index} onClick={() => setCurrentPage(page)} className=" border ">
            {page}
          </button>
        );
      })}{" "}
    </div>
  );
}
