import React from "react";
import TopBar from "../TopBar";
import Mainbar from "../Mainbar";
import { useState } from "react";
import { useEffect } from "react";
import { Link } from "react-router-dom";
export default function AdminPanel() {
  const [searchTerm, setSearchTerm] = useState("");
  const [categories, setCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
const [filteredSubCategories, setFilteredSubCategories] = useState([]);
  //request
  const [recipeData, setRecipeData] = useState({
    name: "",
    description: "",
    image: null,
    ingredients: [],
    prep_time: 0,
    calories: 0,
    vegetarian: false,
    diet: "",
    category: "",
    subcategory: "",
  });

  // Function to fetch recipes based on the search term
  const fetchRecipes = async () => {
    try {
      const response = await fetch(
        `http://localhost:8080/categories?name=${searchTerm}`
      );
      const data = await response.json();
      console.log(data);
      if (data && data.documents && Array.isArray(data.documents)) {
        setCategories(data.documents); // Assuming 'documents' contains the array of recipes
      }
      const response2 = await fetch(
        `http://localhost:8080/subcategories?name=${searchTerm}`
      );
      const data2 = await response2.json();
      console.log(data2);
      if (data2 && data2.documents && Array.isArray(data2.documents)) {
        setSubCategories(data2.documents); // Assuming 'documents' contains the array of recipes
      }
    } catch (error) {
      console.error("Error fetching recipes:", error);
    }
  };
  console.log(categories);

  // Update filtered recipes when the search term or recipes change
  useEffect(() => {
    setFilteredCategories(
      categories.filter((category) =>
        category.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    ); setFilteredSubCategories(
      categories.filter((subCategories) =>
        setCategories.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  }, [searchTerm, categories, subCategories]);

  // Handle input change
  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  console.log(filteredCategories);

  return (
    <div>
      <div>
        <TopBar />
        <Mainbar />
      </div>
      <input
        type="text"
        placeholder="Search for recipes..."
        value={searchTerm}
        onChange={handleInputChange}
      />
      <input
        type="text"
        placeholder="Search for recipes..."
        value={searchTerm}
        onChange={handleInputChange}
      />
      <button onClick={fetchRecipes}>Search</button>

      <ul>
        {filteredCategories.map((category) => (
          <li key={category._id}>
            <Link to={`/recipe/${category._id}`}> {category.name} </Link>
          </li>
          // Render other recipe details as needed
        ))}
      </ul>
    </div>
  );
}

