import React, { useState, useEffect } from "react";
import TopBar from "../TopBar";
import Mainbar from "../Mainbar";
import CreateRecipe from "./CreateRecipe";
import DeleteRecipe from "./DeleteRecipe";
import { Link } from "react-router-dom";

export default function AdminPanel() {

  const [showRecipe, setShowRecipe] = useState(false);

  const handleRecipe = () => {
    setShowRecipe(!showRecipe);
  }

  const handleDelete = () => {
    setShowRecipe(false);
  }




  const buttonStyle = {
    padding: '8px 16px',
    margin: '8px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  };

  const borderStyle = {
    borderBottom: '2px solid #00FF00',
  };

  // Your component code...
  return (
    <div>
      <div>
        <TopBar />
        <Mainbar />
      </div>
      <div className={!showRecipe? "justify-center align-middle": null}>
        <div className="w-1/2 text-center">
          <button style={buttonStyle} onClick={handleRecipe}>Create Recipe</button>
          {showRecipe ? <CreateRecipe /> : null}
        </div>
        <div>
          <button style={showRecipe ? null : borderStyle} onClick={handleDelete}>Delete Recipe</button>
          {!showRecipe ? <DeleteRecipe /> : null}
        </div>
      </div>
    </div>
  );
}
