import React from "react";
import RecipeRating from "./RecipeRating";
import StarRating from "./StarRating";
import { useState } from "react";
import { useAuth } from "../../pages/TokenContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleUser } from "@fortawesome/free-solid-svg-icons";

export default function Reviews({ reviews, recipeId }) {
        const { token } = useAuth();
        const [rating, setRating] = useState(0);
        const [review, setReview] = useState("");
        const [editingReviewId, setEditingReviewId] = useState(null); // Track the ID of the review being edited
        const [rev, setReev] = useState(reviews);

        const handleRatingSelection = (selectedRating) => {
          setRating(selectedRating);
        };

        const handleReviewSubmit = () => {
          if (editingReviewId) {
            // If editingReviewId is not null, it means we're editing an existing review
            fetch(`http://localhost:8080/reviews/${editingReviewId}`, {
              method: "PUT",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
              },
              body: JSON.stringify({
                title: review,
                rating: rating,
                recipe: recipeId,
              }),
            })
              .then((response) => response.json())
              .then((data) => {
                console.log("Review Updated:", data);
                setRating(0);
                setReview("");
                setEditingReviewId(null);

              })
              .catch((error) => {
                console.error("Error updating review:", error);
                // Handle errors or show error message to the user
              });
          } else {
            // If editingReviewId is null, it means we're adding a new review
            fetch("http://localhost:8080/reviews", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
              },
              body: JSON.stringify({
                title: review,
                rating: rating,
                recipe: recipeId,
              }),
            })
              .then((response) => response.json())
              .then((data) => {
                console.log("Review Created:", data);
                setRating(0);
                setReview("");
                // Handle success or update UI as needed
              })
              .catch((error) => {
                console.error("Error creating review:", error);
                // Handle errors or show error message to the user
              });
          }
        };

        const startEditingReview = (reviewId, reviewTitle, reviewRating) => {
          // Function to populate the fields for editing a review
          setReview(reviewTitle);
          setRating(reviewRating);
          setEditingReviewId(reviewId);
        };

        const cancelEditing = () => {
          // Function to cancel the editing mode
          setReview("");
          setRating(0);
          setEditingReviewId(null);
        };

  return (
    <div className="">
     <h1>{editingReviewId ? "Edit Review" : "Add Reviews"}</h1>
      <div>
        <RecipeRating onStarClick={handleRatingSelection} />
        <div>
          <textarea
            className={`border-2 border-gray-300 rounded-md p-2 w-1/2 ${
              rating > 0 ? "block" : "hidden"
            }`}
            placeholder="Write your review here..."
            value={review}
            onChange={(event) => setReview(event.target.value)}
          />
          <button
            className={`bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded ${
              rating > 0 ? "block" : "hidden"
            }`}
            onClick={handleReviewSubmit}

          >
            Submit
          </button>
        </div>
       <div>
        {reviews.map((review) => (
          <div
            key={review.id}
            className="border-2 rounded-md p-2 my-2 bg-zinc-100 shadow-md hover:border-green-300 transition-all duration-200 z-1 w-10/12"
          >
            <div className="flex items-center">
              <FontAwesomeIcon icon={faCircleUser} className="mr-4 text-green-800 text-3xl" />
              <span className="font-normal text-slate-900 uppercase drop-shadow-lg">{review.title}</span>
              {/* Edit button for each review */}
              <button
                onClick={() => startEditingReview(review._id, review.title, review.rating)}
                className="ml-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Edit
              </button>
            </div>
            <div className="flex items-center mt-2">
              <StarRating average={review.rating} quantity={1} />
            </div>
          </div>
        ))}
      </div>
      </div>
      {editingReviewId && (
        <button
          onClick={cancelEditing}
          className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
        >
          Cancel Edit
        </button>
      )}
    </div>
  );
}
