import React from "react";
import { useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useState } from "react";
export default function FetchColllection({
  collections,
  setRecipesData,
  recipesData,
  currentPosts,
}) {
        const [deletedRecipeId, setDeletedRecipeId] = useState(null);
const token = localStorage.getItem("token");
  useEffect(() => {
    async function fetchRecipeInfo(id) {
      try {
        const response = await axios.get(`http://localhost:8080/recipes/${id}`);
        return response.data;
      } catch (error) {
        console.error(`Error fetching recipe with ID ${id}:`, error);
        return null;
      }
    }

    async function fetchRecipesForCollections() {
      const recipesDataObj = {};

      for (const collection of collections) {
        const collectionRecipes = [];

        for (const recipeId of collection.recipes) {
          const recipeInfo = await fetchRecipeInfo(recipeId);
          collectionRecipes.push(recipeInfo);
        }

        recipesDataObj[collection.name] = collectionRecipes;
      }

      setRecipesData(recipesDataObj);
    }

    fetchRecipesForCollections();
  }, [deletedRecipeId]);


  async function deleteRecipeFromCollection(collectionId, recipeId) {
        try {
          await axios.delete(`http://localhost:8080/collections/${collectionId}/recipes`, {
            headers: {
              Authorization: `Bearer {token}`,
              'Content-Type': 'application/json'
            },
            data: {
              recipe: recipeId
            }
          });
          setDeletedRecipeId(recipeId); 
          console.log(`Recipe with ID ${recipeId} deleted from collection with ID ${collectionId}`);
        } catch (error) {
          console.error(`Error deleting recipe with ID ${recipeId} from collection with ID ${collectionId}:`, error);
        }
      }


  return (
    <div>
      {currentPosts.map((el) => (
        <div
          key={el._id}
          className="flex items-center px-2 py-4 bg-neutral-50 rounded-md shadow-md"
        >
          {recipesData[el.name] &&
            recipesData[el.name].map((recipe) => (
              <div key={recipe.document._id} className=" w-1/4 ml-4">
                <Link to={`/recipe/${recipe.document._id}`}>
                  <img
                    src={recipe.document.image}
                    alt={recipe.document.name}
                    className=" rounded-md shadow-xl hover:translate-y-2 transform transition-all duration-500 ease-in-out"
                  />
                </Link>
                <button onClick={()=> deleteRecipeFromCollection(el._id, recipe.document._id)}>
                        trash
                </button>
              </div>
            ))}
        </div>
      ))}
    </div>
  );
}
