import CategoriesList from "./CategoriesList";
import RecipesNav from "./RecipesNav";
import mainfruite from "../../assets/images/mainfruite.png";
import { useLoaderData } from "react-router-dom";
import { Link } from "react-router-dom";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAdd } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { useEffect } from "react";

export default function RecipesDetail() {
  const token = localStorage.getItem("token");
  const data = useLoaderData();

  const [showDropdown, setShowDropdown] = useState(false);
  const [hiddenCollections, setHiddenCollections] = useState([]);

  const showAllCategories = () => {
    setShowDropdown(!showDropdown);
  };

  console.log(data);
  const {
    document: { document },
  } = data || {};

  const {
    additionalData: { documents },
  } = data || {};
  const {
    usersList: {
      document: { collections },
    },
  } = data;


  useEffect(() => {
    // Clear hiddenCollections when a new recipe is loaded
    setHiddenCollections([]);
  }, [document._id]);

  const addRecipeToCollection = async (id, recipeId, token) => {
    const url = `http://localhost:8080/collections/${id}/recipes`;

    try {
      const response = await axios.put(
        url,
        {
          recipe: recipeId,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );


      setHiddenCollections((prevHiddenCollections) => [
        ...prevHiddenCollections,
        id,
      ]);

      return response.data;
    } catch (error) {
      // Handle any errors appropriately
      console.error("Error adding recipe to collection:", error);
      throw error;
    }
  };
  const isHidden = (id) => hiddenCollections.includes(id);
  return (
    <div className="flex w-full relative">
      <CategoriesList width="w-1/4" />
      <div className="w-2/3 p-8">
        <RecipesNav />
        <div className="w-full">
          <h1 className="text-4xl font-bold text-green-600 mb-4">{document.name}</h1>

          <div className="flex justify-between items-start">
            <img
              className="w-64 h-64 object-cover rounded-lg shadow-lg"
              src={document.image}
              alt={document.name}
            />
            <div className="w-1/2 ml-4">
              <h2 className="text-xl font-semibold text-green-600 mb-4">{document.description}</h2>
              <div className="flex justify-between mb-4">
                <div>
                  <h2 className="text-2xl font-semibold text-green-600 mb-2">Ingredients</h2>
                  <ul className="text-md font-light">
                    {document.ingredients.map((el, index) => (
                      <li key={index}>{el}</li>
                    ))}
                  </ul>
                </div>
                <div className="flex flex-col">
                  <p className="font-semibold text-green-600 mb-2">
                    Calories <span className="text-black">{document.calories}</span>
                  </p>
                  <p className="font-semibold text-green-600 mb-2">
                    Time <span className="text-black">{document.prep_time}</span>
                  </p>
                </div>
              </div>
            </div>
          </div>

          <div className="flex justify-between mt-8">
            <div className="flex flex-col w-1/2">
              {documents.map(
                (el) =>
                  el._id !== document._id && (
                    <div key={el._id} className="mb-4">
                      <Link
                        to={`/recipe/${el._id}`}
                        className="border-2 border-green-600 rounded-3xl p-2 font-semibold hover:text-green-600 transition-all"
                      >
                        {el.name}
                      </Link>
                    </div>
                  )
              )}
            </div>

            <div className="text-center">
              <button className="border-2 border-green-600 rounded-xl p-2 font-semibold hover:text-green-600 transition-all hover:border-black">
                Collection
                <FontAwesomeIcon
                  icon={faAdd}
                  className="text-green-500 pl-2 text-lg"
                />
              </button>
              {showDropdown && (
                <div className="flex flex-col mt-4 w-1/2">
                  {collections
                    .filter(
                      (collection) =>
                        !collection.recipes.some(
                          (recipe) => recipe === document._id
                        )
                    )
                    .map((collection) => (
                      <div
                        key={collection._id}
                        className={`px-2 ${
                          isHidden(collection._id) ? "hidden" : ""
                        }`}
                      >
                        <button
                          onClick={() =>
                            addRecipeToCollection(
                              collection._id,
                              document._id,
                              token
                            )
                          }
                          className="border-2 border-green-600 rounded-3xl p-2 font-semibold hover:text-green-600 transition-all"
                        >
                          {collection.name}
                        </button>
                      </div>
                    ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <img src={mainfruite} alt="" className="w-1/4 h-screen object-cover" />
    </div>
  );
}
