import React from 'react';
import RecipeRating from './RecipeRating';

export default function Reviews({ reviews }) {
        const [rating, setRating] = useState(0);
  const handleRatingSelection = (selectedRating) => {
    // Send the selected rating to the API endpoint
    // Replace 'YOUR_JWT_TOKEN' with your actual JWT token
    fetch('/reviews', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer YOUR_JWT_TOKEN`,
      },
      body: JSON.stringify({
        title: 'Your Review Title',
        rating: selectedRating,
        recipe: 'Your Recipe Description',
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Review Created:', data);
        // Handle success or update UI as needed
      })
      .catch((error) => {
        console.error('Error creating review:', error);
        // Handle errors or show error message to the user
      });
  };

  return (
    <div className=''>
      <h1>Add Reviews</h1>
      <div>
        <RecipeRating onStarClick={handleRatingSelection} />
      </div>
    </div>
  );
}
