import { useLoaderData, Link } from "react-router-dom";
import { useState } from "react";
import Pagination from "../Pagination";
import logo from "../../assets/images/s1.jpg";

export default function RecipesCard() {
  const data = useLoaderData();

  const {
    recipesList: { documents },
  } = data || {};

  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setpostPerPage] = useState(8);

  const indexOfLastPost = currentPage * postsPerPage;

  const indexOfFirstPost = indexOfLastPost - postsPerPage;

  const currentPosts = documents.slice(indexOfFirstPost, indexOfLastPost);

  console.log(documents[0]._id);

  return (
    <div className="">
      <h1 className="mt-8 font-montez text-4xl text-green-300">Cussines</h1>
      <div className="grid grid-cols-3 grid-rows-2 gap-8 ">
        {currentPosts.map((el) => (
          <div key={el._id} className="">
            <div>
              <img src={el.image} alt="" className="w-300px h-200px " />
            </div>
            <div className="pl-4">
              <h1>{el.name}</h1>
              {/* <p>{el.description}</p> */}
              <Link to={`details/${el._id}`.toString()}>Read more</Link>
              <image src={el.image} alt="" />
            </div>
          </div>
        ))}
      </div>
      <Pagination
        totalRes={documents.length}
        postsPerPage={postsPerPage}
        setCurrentPage={setCurrentPage}
      />
    </div>
  );
}
