import { useLoaderData, Link } from "react-router-dom";

export default function Catlist() {
  const data = useLoaderData();
  return (
    <div className=" p-10  pl-24">
      <h1 className="pb-4 font-montez text-4xl text-green-100
      ">Filter recipes</h1>

      <h2 className="pt-4 text-xl text-green-200  ">Cusine</h2>
      {data.documents.map((el) => (
        <div key={el._id} className="text-md pl-2 font-light ho">
          <Link to={`details/${el._id}`.toString()}>{el.name}</Link>
        </div>
      ))}

      <h2 className="pt-4 text-xl text-green-200 ">Dite</h2>
      {data.documents.map((el) => (
        <div key={el._id} className="text-md pl-2 font-light">
          <Link to={`/details`}>{el.name}</Link>
        </div>
      ))}
      <h2 className="pt-4 text-xl text-green-200 ">Goals</h2>
      {data.documents.map((el) => (
        <div key={el._id} className="text-md pl-2 font-light">
          <Link to={`/details`}>{el.name}</Link>
        </div>
      ))}
    </div>
  );
}

export const catLoader = async () => {
  const res = await fetch("http://localhost:8080/categories");

  if (!res.ok) {
    throw new Error("Something went wrong");
  }
  return res.json();
};
