import { useLoaderData, Link } from "react-router-dom";
import Pagination from "../Pagination";
import logo from "../../assets/images/s1.jpg";

export default function RecipesCard({currentPage, setCurrentPage, postsPerPage,documents}) {


  const indexOfLastPost = currentPage * postsPerPage;

  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const filterRecipes = documents.filter(
    (el) =>
      el.category !== "65678b30e66dda14da9127c5" &&
      el.category !== "656131c155fa025551457e36"
  );

  const currentPosts = filterRecipes.slice(indexOfFirstPost, indexOfLastPost);

  console.log(currentPage, postsPerPage, currentPosts);
  return (
    <div className="">
      <h1 className="mt-8 font-montez text-4xl text-green-300">Cussines</h1>
      <div className="grid grid-cols-3 grid-rows-1 gap-8 ">
        {currentPosts.map((el) => (
          <div key={el._id} className="">
            <div>
              <img src={el.image} alt="" className="w-250px h-250px " />
            </div>
            <div className="pl-4">
              <h1>{el.name}</h1>
              {/* <p>{el.description}</p> */}
              <Link to={`details/${el._id}`.toString()}>Read more</Link>
              <image src={el.image} alt="" />
            </div>
          </div>
        ))}
      </div>

    </div>
  );
}
