import {  useLoaderData } from "react-router-dom";
import CategoriesList from "./CategoriesList";
import RecipesNav from "./RecipesNav";
import mainfruite from "../../assets/images/mainfruite.png";
import { useState } from "react";
import Pagenation from "../Pagination";
import { Link } from "react-router-dom";

export default function Catdetails() {

  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setpostPerPage] = useState(2);
  const indexOfLastPost = currentPage * postsPerPage;

  const indexOfFirstPost = indexOfLastPost - postsPerPage;



  const data = useLoaderData();

  const {
    document: {
      document: { description },
    },
    additionalData: { documents },
  } = data || {};

  console.log(data);
  const {
    document: { document },
  } = data || {}; 

  const currentPosts = documents.slice(indexOfFirstPost, indexOfLastPost);
  return (
    <div className="flex w-full relative">
      <CategoriesList width={"w-1/4 "} />
      <div className=" w-2/3 ">
        <RecipesNav />
        <div className="">
          <h1 className="text-2xl font-serif text-green-100 pt-8   drop-shadow-lg ">
            {description}
          </h1>
          <div className="mt-8"><h1 className="">{document.name} Recipes</h1></div>
          <div className="flex justify-between">
            <div className="flex flex-wrap mt-8">
              {currentPosts.map((el) => (
                <div key={el._id} className="flex justify-between  px-2  ">


                  <div className="w-100px h-100px bg-slate-400">
                    <Link to={`/recipe/${el._id}`}>
                      <img
                        className="w-full h-full border rounded-lg shadow-lg"
                        src={el.image}
                        alt={el.name}
                      />
                      <p className="text-xl font-montez text-green-100 pt-2   drop-shadow-lg ">
                        {el.name}
                      </p>
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div c>
          <Pagenation
            totalRes={documents.length}
            postsPerPage={postsPerPage}
            setCurrentPage={setCurrentPage}
            currentPage={currentPage}
          />
        </div>
      </div>
      <img src={mainfruite} alt="" className="w-1/4 h-1000px bg-" />
    </div>
  );
}
