export const TokenProvider = ({ children, initToken }) => {
  const [token, setToken] = useState("lol");



  const handleLogout = () => {
    setToken(null);
  };

  const contextValue = {
    token,
        setToken,       
    handleLogout,
  };

  return (
    <TokenContext.Provider value={token}>
       {children}
      <Outlet />
    </TokenContext.Provider>
  );
}
