import { useLoaderData, Link } from "react-router-dom";
import { useState } from "react";
import Pagination from "../Pagination";

export default function RecipesCard() {
  const data = useLoaderData();

  const {
    recipesList: { documents },
  } = data || {};

  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setpostPerPage] = useState(1);

  const indexOfLastPost = currentPage * postsPerPage;

  const indexOfFirstPost = indexOfLastPost - postsPerPage;

  const currentPosts = documents.slice(indexOfFirstPost, indexOfLastPost);

  console.log(currentPosts);
  return (
    <div className="">
      <div>
        {currentPosts.map((el) => (
          <div key={el._id} className="flex">
            <div className="w-1/3">
              <img src={el.image} alt="" />
            </div>
            <div className="w-1/3">
              <h1>{el.title}</h1>
              <p>{el.description}</p>
              <Link to={`details/${el._id}`.toString()}>Read more</Link>
            </div>
          </div>
        ))}
      </div>
      <Pagination
        totalRes={documents.length}
        postsPerPage={postsPerPage}
        setCurrentPage={setCurrentPage}
      />
    </div>
  );
}
