import React, { useState, useEffect } from "react";
import TopBar from "../TopBar";
import Mainbar from "../Mainbar";
import { Link } from "react-router-dom";

export default function AdminPanel() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchTerm2, setSearchTerm2] = useState("");
  const [categories, setCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [filteredSubCategories, setFilteredSubCategories] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);
  const [selectedSubCategoryId, setSelectedSubCategoryId] = useState(null);

  const [recipeData, setRecipeData] = useState({
    name: "",
    description: "",
    image: null,
    ingredients: [],
    prep_time: 0,
    calories: 0,
    vegetarian: false,
    diet: "",
    category: "",
    subcategory: "",
  });

  // Function to fetch categories and subcategories based on search term
  const fetchCatSub = async () => {
    try {
      const response = await fetch(
        `http://localhost:8080/categories?name=${searchTerm}`
      );
      const data = await response.json();
      if (data && data.documents && Array.isArray(data.documents)) {
        setCategories(data.documents);
      }

      const response2 = await fetch(
        `http://localhost:8080/subcategories?name=${searchTerm2}`
      );
      const data2 = await response2.json();
      if (data2 && data2.documents && Array.isArray(data2.documents)) {
        setSubCategories(data2.documents);
      }
    } catch (error) {
      console.error("Error fetching categories and subcategories:", error);
    }
  };

  // Update filtered categories and subcategories based on search terms
  useEffect(() => {
    setFilteredCategories(
      categories.filter((category) =>
        category.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
    setFilteredSubCategories(
      subCategories.filter((subCategory) =>
        subCategory.name.toLowerCase().includes(searchTerm2.toLowerCase())
      )
    );
  }, [searchTerm, searchTerm2, categories, subCategories]);

  // Handle input change for categories
  const handleCategoryInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Handle input change for subcategories
  const handleSubCategoryInputChange = (event) => {
    setSearchTerm2(event.target.value);
  };

  const createRecipe = async () => {
    try {
      const response = await fetch("http://localhost:8080/recipes", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          token: localStorage.getItem("token"),
        },
        body: JSON.stringify({
          name: recipeData.name,
          description: recipeData.description,
          // Add other recipe details...
          category: selectedCategoryId,
          subcategory: selectedSubCategoryId,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Recipe created:", data.document);
        // Reset the recipe form or perform other actions on successful creation
      } else {
        console.error("Failed to create recipe");
      }
    } catch (error) {
      console.error("Error creating recipe:", error);
    }
  };

  return (
    <div>
      <div>
        <TopBar />
        <Mainbar />
      </div>

      <button onClick={fetchCatSub}>Search</button>

      {/* Select category */}
      <select onChange={(e) => setSelectedCategoryId(e.target.value)}>
        <option value="">Select Category</option>
        {filteredCategories.map((category) => (
          <option key={category._id} value={category._id}>
            {category.name}
          </option>
        ))}
      </select>

      {/* Select subcategory */}
      <select onChange={(e) => setSelectedSubCategoryId(e.target.value)}>
        <option value="">Select Subcategory</option>
        {filteredSubCategories.map((subcategory) => (
          <option key={subcategory._id} value={subcategory._id}>
            {subcategory.name}
          </option>
        ))}
      </select>

      <button onClick={createRecipe}>Create Recipe</button>
    </div>
  );
}

