import React from "react";
import TopBar from "../TopBar";
import Mainbar from "../Mainbar";

export default function AdminPanel() {


        const [searchTerm, setSearchTerm] = useState('');
        const [recipes, setRecipes] = useState([]);
        const [filteredRecipes, setFilteredRecipes] = useState([]);

        // Function to fetch recipes based on the search term
        const fetchRecipes = async () => {
          try {
            const response = await fetch(`http://localhost:8080/recipes?name=${searchTerm}`);
            const data = await response.json();
            setRecipes(data); // Assuming the API returns an array of recipes
          } catch (error) {
            console.error('Error fetching recipes:', error);
          }
        };

        // Update filtered recipes when the search term or recipes change
        useEffect(() => {
          setFilteredRecipes(
            recipes.filter(recipe => recipe.name.toLowerCase().includes(searchTerm.toLowerCase()))
          );
        }, [searchTerm, recipes]);

        // Handle input change
        const handleInputChange = event => {
          setSearchTerm(event.target.value);
        };

        return (
          <div>
                div
            <input
              type="text"
              placeholder="Search for recipes..."
              value={searchTerm}
              onChange={handleInputChange}
            />
            <button onClick={fetchRecipes}>Search</button>

            <ul>
              {filteredRecipes.map(recipe => (
                <li key={recipe.id}>{recipe.name}</li>
                // Render other recipe details as needed
              ))}
            </ul>
          </div>
        );
      }
