import CategoriesList from "./CategoriesList";
import RecipesNav from "./RecipesNav";
import mainfruite from "../../assets/images/mainfruite.png";
import { useLoaderData } from "react-router-dom";
import { Link } from "react-router-dom";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAdd } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { useEffect } from "react";

export default function RecipesDetail() {
  const token = localStorage.getItem('token');
  const data = useLoaderData();

  const [showDropdown, setShowDropdown] = useState(false);
  const [hiddenCollections, setHiddenCollections] = useState([]);
  const [imagesLoaded, setImagesLoaded] = useState([]);

  const showAllCategories = () => {
    setShowDropdown(!showDropdown);
  };

  useEffect(() => {
    if (data && data.document && data.additionalData) {
      setHiddenCollections([]);
      setImagesLoaded(Array(data.additionalData.documents.length).fill(false));
    }
  }, [data]);

  const addRecipeToCollection = async (id, recipeId, token) => {
    const url = `http://localhost:8080/collections/${id}/recipes`;

    try {
      const response = await axios.put(
        url,
        {
          recipe: recipeId,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );


      setHiddenCollections((prevHiddenCollections) => [
        ...prevHiddenCollections,
        id,
      ]);

      return response.data;
    } catch (error) {
      // Handle any errors appropriately
      console.error("Error adding recipe to collection:", error);
      throw error;
    }
  };
  const isHidden = (id) => hiddenCollections.includes(id);

  return (
    <div className="flex md:flex-row">
      <CategoriesList width="w-1/4" />
      <div className="w-full md:w-3/4">
        <RecipesNav />
        <div className="w-full md:flex">
          <div className="md:w-1/2 w-100px">
            <h1 className="text-4xl font-bold text-green-500 pt-8 mb-4">{document.name}</h1>
            <img
              className="w-full h-auto border rounded-lg shadow-lg mb-4 md:hidden"
              src={document.image}
              alt={document.name}
            />
            <div className="hidden md:block">
              <img
                className="w-full h-auto border rounded-lg shadow-lg mb-4"
                src={document.image}
                alt={document.name}
              />
            </div>
          </div>
          <div className="md:w-1/2 md:pl-8 mt-0 md:mt-40">
            <h2 className="text-lg font-semibold text-green-500 mb-4">{document.description}</h2>
            <div className="flex justify-between">
              <div>
                <h2 className="text-xl font-semibold text-green-500 mb-4">Ingredients</h2>
                <ul className="text-base font-light">
                  {document.ingredients.map((el) => (
                    <li key={el._id}>{el}</li>
                  ))}
                </ul>
              </div>
              <div className="flex pt-8 px-4">
                <p className="font-semibold text-green-500 px-4 text-lg">
                  Calories <span className="text-black">{document.calories}</span>
                </p>
                <p className="font-semibold text-green-500 px-4 text-lg">
                  Time <span className="text-black">{document.prep_time}</span>
                </p>
              </div>
            </div>
          </div>
        </div>

        <div className="flex flex-col items-center relative  ">
        <div className="w-full">
          <h2 className="text-2xl font-semibold text-green-500 mb-4">Related recipes</h2>
        </div>
          <div className="w-200px md:w-full flex justify-normal bg-zinc-50 shadow-lg rounded-lg">
            {documents.map(
              (el) =>
                el._id !== document._id && (
                  <div key={el._id} className="flex justify-between px-2 mb-2 w-150px">
                    <Link
                      to={`/recipe/${el._id}`}
                      className="rounded-3xl p-2 font-poppins hover:text-green-500 transition-all"
                    >
                      <img src={el.image} alt={el.image} className="border-2 rounded-md hover:border-green-100 h-full w-full"/>

                    </Link>

                  </div>
                )
            )}
          </div>

          <div onClick={showAllCategories} className="text-center mt-4 ">
            <button className="border-2 border-green-100 rounded-xl p-2 font-semibold hover:text-green-500 transition-all hover:border-black">
              Collection
              <FontAwesomeIcon icon={faAdd} className="text-green-500 pl-2 text-lg" />
            </button>
            {showDropdown && (
              <div className="w-full ">
                {collections
                  .filter(
                    (collection) => !collection.recipes.some((recipe) => recipe === document._id)
                  )
                  .map((collection) => (
                    <div
                      key={collection._id}
                      className={`flex justify-between px-2 ${isHidden(collection._id) ? "hidden" : ""}`}
                    >
                      <button
                        onClick={() => addRecipeToCollection(collection._id, document._id, token)}
                        className="border-2 border-green-100 rounded-3xl p-2 font-poppins hover:text-green-500 transition-all"
                      >
                        {collection.name}
                      </button>
                    </div>
                  ))}
              </div>
            )}
          </div>
        </div>
      </div>
      <img src={mainfruite} alt="" className="w-full md:w-1/4 h-1000px bg-" />
    </div>
  );
}
