import CategoriesList from "./CategoriesList";
import RecipesNav from "./RecipesNav";
import mainfruite from "../../assets/images/mainfruite.png";
import { useLoaderData } from "react-router-dom";
import { Link } from "react-router-dom";

export default function RecipesDetail() {
  const data = useLoaderData();

  const {collectionDrop, setCollectionDrop} = useState(false);

  console.log(data);
  const {
    document: { document },
  } = data || {};

  const {
    additionalData: { documents },
  } = data || {
  }
  const {
    usersList: {
      document: { collections }
    }
  } = data;

  const dropCollection = collections.map((el) => {
    setCollectionDrop();
  });
  console.log(collections);
  return (
    <div className="flex w-full relative">
      <CategoriesList width={"w-1/4 "} />
      <div className=" w-2/3 ">
        <RecipesNav />
        <div className="w-full">
          <h1 className="text-4xl font-montez text-green-100 pt-8   drop-shadow-lg ">
            {document.name}
          </h1>

          <div className="flex justify-between pt-4">
            <img className="w-400px h-1/2 border rounded-lg shadow-lg" src={document.image} alt={document.name} />
            <div className="w-1/2">
              <h2 className="text-xl font-serif text-green-100 pt-8   drop-shadow-lg ">
                {document.description}
              </h2>
              <div className="flex justify-between">
                <div>
                  <h2 className="text-2xl font-montez text-green-100 pt-8   drop-shadow-lg ">
                    Ingredients
                  </h2>
                  <ul className="text-md font-light ">
                    {document.ingredients.map((el) => (
                      <li key={el._id}>{el}</li>
                    ))}
                  </ul>
                </div>
                <p>sadd</p>
                <div className="flex pt-8 px-4">
                  <p className="font-montez text-green-100 px-4 text-xl">claories <p className="
                  text-black">{document.calories}</p></p>
                  <p className="font-montez text-green-100 px-4 text-xl">time <p className=" text-black">{document.prep_time}</p></p>

                </div>


              </div>

            </div>

          </div>

          <div className="flex mt-8 bg-slate-300 w-1/2">
            {documents.map((el) => (
              el._id !== document._id &&
              <div key={el._id} className="flex justify-between  px-2  ">
                <Link to={`/recipe/${el._id}`} className="border-2 border-green-100 rounded-3xl p-2 font-poppins hover:text-green-100 transition-all ">{el.name}</Link>
              </div>
            )
            ) }

          </div>
          <button onClick={dropCollection}>add</button>
        </div>

      </div>
      <img src={mainfruite} alt="" className="w-1/4 h-1000px bg-" />
    </div>
  );
}
