import { useLoaderData, Link } from "react-router-dom";

export default function CategoriesList({ width }) {
  const data = useLoaderData();

  const {
    categoriesList: { documents },
    recipesList: { documents: recipes },
  } = data || {};

  return (
    <div className={`p-4 lg:p-10 lg:pl-28 ${width} mt-14`}>
      <h1 className="pb-4 font-semibold text-4xl text-green-500">Filter Recipes</h1>

      <section className="space-y-4">
        <CategorySection title="Cuisine" items={documents} linkPrefix="/" />
        <CategorySection title="Diet" items={recipes.filter((el) => el.diet === "true")} linkPrefix="/recipe" />
        <CategorySection title="Vegetarians" items={recipes.filter((el) => el.vegetarian)} linkPrefix="/recipe" />
      </section>
    </div>
  );
}

// Component to render each category section
function CategorySection({ title, items, linkPrefix }) {
  return (
    <div>
      <h2 className="pt-4 text-lg font-semibold text-green-400">{title}</h2>
      {items.map((el) => (
        <div key={el._id} className="text-base font-light hover:text-green-400 hover:font-semibold pt-1">
          <Link to={`${linkPrefix}/${el._id}`}>{el.name}</Link>
        </div>
      ))}
    </div>
  );
}
