import React, { useState } from "react";
import { Link } from "react-router-dom";
export default function Dropdown({show}) {
  const categories = ["All", "Food", "Drinks", "Desserts"];



  return (
    <div>
      <div onClick={show}>
      </div>
      {show && (
        <div>
          {categories.map((category) => (
            <ul className="p-8">
              <li>

                <Link to= "/">{category}</Link>
              </li>
            </ul>
          ))}
        </div>
      )}
    </div>
  );
}
