import React from "react";

export default function Pagenation({ postsPerPage, totalRes, setCurrentPage , isHidden, styles}) {

  const CustomComponent = ({ variant }) => {
    let classNames = 'border rounded p-4'; 

    switch (variant) {
      default:
        classNames += ' bg-gray-300 text-black';
        break;
    }}

  let pages = [];
  for (let i = 1; i <= Math.ceil(totalRes / postsPerPage); i++) {
    pages.push(i);
  }
  return (
    <div className={classNames}>
      {" "}
      {pages.map((page, index) => {
        return <button key={index} onClick={() => setCurrentPage(page)}>{page}</button>;
      })}{" "}
    </div>
  );
}
