import React, { useEffect } from "react";
import { useAuth } from "../pages/TokenContext";
import { Navigate } from "react-router-dom";
import { useLoaderData } from "react-router-dom";
import CategoriesList from "../components/reecipes/CategoriesList";
import mainfruite from "../assets/images/mainfruite.png";
import RecipesNav from "../components/reecipes/RecipesNav";
import userProfile from "../assets/images/userProfile.png";
import Pagenation from "../components/Pagination";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleCheck } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import axios from "axios";

export default function Profile() {
  const [recipesData, setRecipesData] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setpostPerPage] = useState(1);
  const indexOfLastPost = currentPage * postsPerPage;

  const indexOfFirstPost = indexOfLastPost - postsPerPage;

  const data = useLoaderData();

  const {
    profileData: {
      document: { name, email, collections },
    },
  } = data || {};

  const { token } = useAuth();
  console.log(collections);

  useEffect(() => {
    // Your array of collection objects
    const collections = [
      {
        _id: "6570562bb28a65bc41087a8a",
        name: "food",
        recipes: ["65636674d76446a8fa7390f0"]
      },
      {
        _id: "657057a7b28a65bc41087ab2",
        name: "Drinks",
        recipes: [] // Assuming there are no recipes yet in this collection
      }
      // ... other collection objects
    ];

    // Function to fetch recipe information by ID
    async function fetchRecipeInfo(id) {
      try {
        const response = await axios.get(`http://localhost:8080/recipes/${id}`);
        return response.data; // Assuming the response contains the recipe data
      } catch (error) {
        console.error(`Error fetching recipe with ID ${id}:`, error);
        return null; // Or handle the error as needed
      }
    }

    // Function to fetch recipes for each collection
    async function fetchRecipesForCollections() {
      const recipesDataObj = {};

      for (const collection of collections) {
        const collectionRecipes = [];

        for (const recipeId of collection.recipes) {
          const recipeInfo = await fetchRecipeInfo(recipeId);
          collectionRecipes.push(recipeInfo);
        }

        recipesDataObj[collection.name] = collectionRecipes;
      }

      setRecipesData(recipesDataObj);
    }

    fetchRecipesForCollections();
  }, [])



  console.log(recipesData)
  const currentPosts = collections.slice(indexOfFirstPost, indexOfLastPost);

  return (
    <div className="flex w-full relative">
      <CategoriesList width={"w-1/4 "} path={"category"} />
      <div className=" w-2/3 ">
        <RecipesNav />
        <div className=" mt-4  flex-col justify-center items-center ">
          <div className="w-full flex justify-center">
            <img src={userProfile} alt="" className=" w-1/4 h-1000px  " />
          </div>
          <div className=" mt-4 ">
            <h2 className=" font-mono text-2xl text-center text-green-300 drop-shadow">
              {name}{" "}
              <FontAwesomeIcon
                icon={faCircleCheck}
                className="text-blue-400 text-xl"
              />
            </h2>
            <h3 className="text-center font-extralight text-gray-500">
              {email}
            </h3>
          </div>

          <div className="bg-slate-300 mt-20 ">
            <h1 className="font-montez text-green-100 text-3xl">Collection</h1>
            {currentPosts.map((el) => (
              <div
                key={el._id}
                className="flex justify-between items-center px-2 py-4"
              >
                {el.name}
              </div>
            ))}
          </div>
          <Pagenation
            totalRes={collections.length}
            postsPerPage={postsPerPage}
            setCurrentPage={setCurrentPage}
            currentPage={currentPage}
            styles="absolute right-0 top-20 flex flex-col space-y-2"
           name = {collections}
          />
          {console.log(collections)}
        </div>
      </div>
      <img src={mainfruite} alt="" className="w-1/4 h-1000px bg-" />
    </div>
  );
}
