import CategoriesList from "./CategoriesList";
import RecipesNav from "./RecipesNav";
import mainfruite from "../../assets/images/mainfruite.png";
import { useLoaderData } from "react-router-dom";
import { Link } from "react-router-dom";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAdd } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { useEffect } from "react";

export default function RecipesDetail() {
  const token = localStorage.getItem("token");
  const data = useLoaderData();
  const [coll, setcoll] = useState([]);

  const [showDropdown, setShowDropdown] = useState(false);

  const showAllCategories = () => {
    setShowDropdown(!showDropdown);
  };

  console.log(data);
  const {
    document: { document },
  } = data || {};

  const {
    additionalData: { documents },
  } = data || {};
  const {
    usersList: {
      document: { collections },
    },
  } = data;
useEffect(() => {
    async function fetchCollectionInfo(id) {
      try {
        const response = await axios.get(
          `http://localhost:8080/collections/${id}`
        );
        return response.data;
      } catch (error) {
        console.error(`Error fetching collection with ID ${id}:`, error);
        return null;
      }
    }

    async function fetchCollectionsForUser() {
      const collectionsData = [];

      for (const collectionId of collections) {
        const collectionInfo = await fetchCollectionInfo(collectionId);
        collectionsData.push(collectionInfo);
      }

      setcoll(collectionsData);
    }

    fetchCollectionsForUser();
}, [coll]);
  const addRecipeToCollection = async (id, recipeId, token) => {
    const url = `http://localhost:8080/collections/${id}/recipes`;

    try {
      const response = await axios.put(
        url,
        {
          recipe: recipeId,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setcoll((prevCollections) =>
        prevCollections.map((collection) => {
          if (collection._id === id) {
            // Assuming recipes is an array in the collection object
            return {
              ...collection,
              recipes: [...collection.recipes, recipeId], // Add the new recipe ID to the collection
            };
          }
          return collection;
        })
      );

      return response.data; // This might contain the message, document, etc. as described in the response body
    } catch (error) {
      // Handle any errors appropriately
      console.error("Error adding recipe to collection:", error);
      throw error;
    }
  };

  return (
    <div className="flex w-full relative">
      <CategoriesList width={"w-1/4 "} />
      <div className=" w-2/3 ">
        <RecipesNav />
        <div className="w-full">
          <h1 className="text-4xl font-montez text-green-100 pt-8   drop-shadow-lg ">
            {document.name}
          </h1>

          <div className="flex justify-between pt-4">
            <img
              className="w-400px h-1/2 border rounded-lg shadow-lg"
              src={document.image}
              alt={document.name}
            />
            <div className="w-1/2">
              <h2 className="text-xl font-serif text-green-100 pt-8   drop-shadow-lg ">
                {document.description}
              </h2>
              <div className="flex justify-between">
                <div>
                  <h2 className="text-2xl font-montez text-green-100 pt-8   drop-shadow-lg ">
                    Ingredients
                  </h2>
                  <ul className="text-md font-light ">
                    {document.ingredients.map((el) => (
                      <li key={el._id}>{el}</li>
                    ))}
                  </ul>
                </div>
                <p>sadd</p>
                <div className="flex pt-8 px-4">
                  <p className="font-montez text-green-100 px-4 text-xl">
                    claories{" "}
                    <p
                      className="
                  text-black"
                    >
                      {document.calories}
                    </p>
                  </p>
                  <p className="font-montez text-green-100 px-4 text-xl">
                    time <p className=" text-black">{document.prep_time}</p>
                  </p>
                </div>
              </div>
            </div>
          </div>

          <div
            className="flex justify-between relative h-200px
          "
          >
            <div className="flex mt-8  w-1/2 h-50px">
              {documents.map(
                (el) =>
                  el._id !== document._id && (
                    <div key={el._id} className="flex justify-between  px-2  ">
                      <Link
                        to={`/recipe/${el._id}`}
                        className="border-2 border-green-100 rounded-3xl p-2 font-poppins hover:text-green-100 transition-all "
                      >
                        {el.name}
                      </Link>
                    </div>
                  )
              )}
            </div>

            <div onClick={showAllCategories} className="text-center">
              <button className="border-2 border-green-100 rounded-xl p-2 font-semibold hover:text-green-100 transition-all hover:border-black">
                Collection
                <FontAwesomeIcon
                  icon={faAdd}
                  className="text-green-500 pl-2 text-lg"
                />
              </button>
              {showDropdown && (
                <div className="flex mt-8 w-1/2">
                  {collections
                    .filter(
                      (collection) =>
                        !collection.recipes.some(
                          (recipe) => recipe === document._id
                        )
                    )
                    .map((collection) => (
                      <div
                        key={collection._id}
                        className="flex justify-between px-2"
                      >
                        <button
                          onClick={() =>
                            addRecipeToCollection(
                              collection._id,
                              document._id,
                              token
                            )
                          }
                          className="border-2 border-green-100 rounded-3xl p-2 font-poppins hover:text-green-100 transition-all"
                        >
                          {collection.name}
                        </button>
                      </div>
                    ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <img src={mainfruite} alt="" className="w-1/4 h-1000px bg-" />
    </div>
  );
}
