import React from "react";
import { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { TokenContext } from "./TokenContext";
import { Link } from "react-router-dom";
import { Navigate } from "react-router-dom";
import { useNavigate } from "react-router-dom";

export default function Loginpage() {
  const { token, setToken, handleLogout } = useContext(TokenContext);
  const navigate = useNavigate();
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("http://localhost:8080/login", loginData);

      if (res.status === 200) {
        const { token } = res.data;
        setToken(token);
        const { email } = loginData;
        localStorage.setItem("email", email);
        const id = res.data.userId;
        localStorage.setItem("userId", id);
        navigate("/", { replace: true });
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="w-full h-full">
        <div className="w-500px h-500px bg-white mx-auto mt-20 rounded-lg">
          <h1 className="text-2xl text-center font-bold mt-10">Login</h1>
          <div className="grid grid-cols-2 gap-4 mt-10">
            <div className="col-span-2">
              <label
                className="block mb-2 text-sm font-bold text-gray-700"
                htmlFor="email"
              >
                Email
              </label>
              <input
                type="text"
                placeholder="Enter your email"
                name="email"
                id="email"
                value={loginData.email !== null ? loginData.email : ""}
                onChange={handleChange}
                className="w-full px-3 py-2 mb-5 leading-tight text-gray-700 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
              />
            </div>
            <div className="col-span-2">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                placeholder="Enter your password"
                value={loginData.password}
                onChange={handleChange}
                name="password"
                id="password"
                className="w-full border-2 border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-300 focus:border-transparent"
              />
            </div>
            <div className="col-span-2">
              <button className="w-full bg-slate-500 text-white py-2 rounded-md">
                Login
              </button>

              {token && (
                <button
                  onClick={handleLogout}
                  className="w-full bg-slate-500 text-white py-2 rounded-md"
                ></button>
              )}
              <Link to="/">lol</Link>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
}
