import React from "react";

export default function Pagenation({ postsPerPage, totalRes, setCurrentPage , isHidden}) {
  let pages = [];
  for (let i = 1; i <= Math.ceil(totalRes / postsPerPage); i++) {
    pages.push(i);
  }
  return (
    <div className={`${isHidden} absolute left-48 flex-`}>
      {" "}
      {pages.map((page, index) => {
        return <button key={index} onClick={() => setCurrentPage(page)}>{page}</button>;
      })}{" "}
    </div>
  );
}
