import {  useLoaderData } from "react-router-dom";
import CategoriesList from "./CategoriesList";
import RecipesNav from "./RecipesNav";
import mainfruite from "../../assets/images/mainfruite.png";
import { useState } from "react";

export default function Catdetails() {

  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setpostPerPage] = useState(3);
  const indexOfLastPost = currentPage * postsPerPage;

  const indexOfFirstPost = indexOfLastPost - postsPerPage;



  const data = useLoaderData();

  const {
    document: {
      document: { description },
    },
    additionalData: { documents },
  } = data || {};

  console.log(data);
  const {
    document: { document },
  } = data || {}; 

  const currentPosts = document.slice(indexOfFirstPost, indexOfLastPost);
  return (
    <div className="flex w-full relative">
      <CategoriesList width={"w-1/4 "} />
      <div className=" w-2/3 ">
        <RecipesNav />
        <div className="">
          <h1 className="mb-4">{document.name}</h1>
          <h1 className="text-2xl font-serif text-green-100 pt-8   drop-shadow-lg ">
            {description}
          </h1>
          <div className="flex justify-between">
            <div className="flex flex-wrap">
              {documents.map((el) => (
                <div key={el._id} className="flex justify-between  px-2  ">
                  <div className="flex flex-col">
                    <img
                      className="w-400px h-1/2 border rounded-lg shadow-lg"
                      src={el.image}
                      alt={el.name}
                    />
                    <p className="text-xl font-montez text-green-100 pt-8   drop-shadow-lg ">
                      {el.name}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
      <img src={mainfruite} alt="" className="w-1/4 h-1000px bg-" />
    </div>
  );
}
