import { useLoaderData, Link } from "react-router-dom";
import Pagination from "../Pagination";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faC } from "@fortawesome/free-solid-svg-icons";
import { faClockFour } from "@fortawesome/free-solid-svg-icons";

export default function BakeryCard({
  currentPage,
  setCurrentPage,
  postsPerPage,
  indexOfFirstPost,
  indexOfLastPost,
  len,
}) {
  const data = useLoaderData();

  const {
    recipesList: { documents },
  } = data || {};

  const filterRecipes = documents.filter((el) =>
    el
  );

  const currentPosts = document.slice(indexOfFirstPost, indexOfLastPost);

  console.log(documents);
  return (
    <div className="container mx-auto px-8 ">
      <h1 className="font-montez text-4xl text-green-300 mt-2 lg:mt-8">
        Bakery
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {currentPosts.map((el) => (
          <div
            key={el._id}
            className="bg-white rounded-lg overflow-hidden shadow-md transition duration-200 border-2 hover:border-green-200"
          >
            <Link to={`details/${el._id}`}>
              <img
                src={el.image}
                alt={el.name}
                className="w-full h-48 object-cover transition duration-300 grayscale-0 hover:grayscale"
              />
            </Link>
            <div className="p-4">
              <div className="pt-2">
                <div className="flex justify-between">
                  <Link
                    to={`details/${el._id}`}
                    className="text-lg font-semibold text-green-700 hover:text-green-600"
                  >
                    {el.name}
                  </Link>

                  <p className="bg-green-200 text-xs font-semibold py-1 px-2 rounded">
                    <FontAwesomeIcon icon={faC} className="mr-1" />
                    {el.calories}
                  </p>
                </div>
                <p className="text-sm font-light">
                  <FontAwesomeIcon icon={faClockFour} className="mr-1" />
                  {el.prep_time} minutes
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
      <Pagination
        totalRes={len}
        postsPerPage={postsPerPage}
        setCurrentPage={setCurrentPage}
        styles="absolute right-0 top-20 flex flex-col space-y-2"
      />
    </div>
  );
}
