import { useLoaderData, Link } from "react-router-dom";
import React, { createContext, useContext, useEffect, useState } from 'react';
import Pagination from "../Pagination";
const CurrentPostsContext = createContext();

export default function RecipesCard({
  currentPage,
  setCurrentPage,
  postsPerPage,
    indexOfFirstPost,
    indexOfLastPost}) {
  const data = useLoaderData();

  const {
    recipesList: { documents },
  } = data || {};


  const filterRecipes = documents.filter((el) =>
    ["656134f855fa025551457e3c", "656827015cec2c453a58a2b3"].includes(
      el.category
    )
  );



  const [currentPosts, setCurrentPosts] = useState([]);

  const CurrentPostsProvider = ({ children }) => {
    return (
      <CurrentPostsContext.Provider value={currentPosts}>
        {children}
      </CurrentPostsContext.Provider>
    );
  };
  const slicedCurrentPosts = filterRecipes.slice(indexOfFirstPost, indexOfLastPost);

  React.useEffect(() => {
    setCurrentPosts(slicedCurrentPosts);
  }, [slicedCurrentPosts]);

  console.log(cu);
  return (
    <div className="">
      <h1 className="mt-8 font-montez text-4xl text-green-300">Cussines</h1>
      <div className="grid grid-cols-3 grid-rows- gap-8 ">
        {slicedCurrentPosts.map((el) => (
          <div key={el._id} className="">
            <div>
              <img src={el.image} alt="" className="w-250px h-250px " />
            </div>
            <div className="pl-4">
              <h1>{el.name}</h1>
              {/* <p>{el.description}</p> */}
              <Link to={`details/${el._id}`.toString()}>Read more</Link>
              <image src={el.image} alt="" />
            </div>
          </div>
        ))}
      </div>
      <Pagination
        totalRes={filterRecipes.length}
        postsPerPage={postsPerPage}
        setCurrentPage={setCurrentPage}
      />
    </div>
  );
}
