import React, { useState, useEffect } from "react";
import TopBar from "../TopBar";
import Mainbar from "../Mainbar";
import CreateRecipe from "./CreateRecipe";
import DeleteRecipe from "./DeleteRecipe";
import { Link } from "react-router-dom";

export default function AdminPanel() {
  const [showCreateRecipe, setShowCreateRecipe] = useState(false);

  const handleCreateRecipe = () => {
    setShowCreateRecipe(!showCreateRecipe);
  };

  const handleDeleteRecipe = () => {
    setShowCreateRecipe(false);
  };

  const buttonStyle = {
    padding: "8px 16px",
    margin: "8px",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    backgroundColor: "#f0f0f0", // Adjust background color
    color: "#333", // Adjust text color
  };

  const centerDivStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Adjust the height if needed
  };

  return (
    <div>
      <div>
        <TopBar />
        <Mainbar />
      </div>
      <div style={centerDivStyle}>
        {showCreateRecipe ? <CreateRecipe /> : <DeleteRecipe />}
      </div>
      <div style={centerDivStyle}>
        <button
          style={showCreateRecipe ? null : buttonStyle}
          onClick={handleDeleteRecipe}
        >
          Delete Recipe
        </button>
        <button
          style={showCreateRecipe ? buttonStyle : null}
          onClick={handleCreateRecipe}
        >
          Create Recipe
        </button>
      </div>
    </div>
  );
}
