import React from "react";
import { useState } from "react";
import axios from "axios";

export const AutContext = React.createContext(null);

export default function Loginpage() {


  const [token, setToken] = useState("");
  const handleLogout = () => {
    setToken(null);
  };

  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData(() => ({
      ...loginData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("http://localhost:8080/login", loginData);

      if (res.status === 200) {
        setToken(res.data.token);
      }
    } catch (error) {
      console.error(error);
    }
  };



  return (
    <AutContext.Provider value={{ token }}>
    <form onSubmit={handleSubmit}>
      <div className="w-full h-full">
        <div className="w-500px h-500px bg-white mx-auto mt-20 rounded-lg">
          <h1 className="text-2xl text-center font-bold mt-10">Login</h1>
          <div className="grid grid-cols-2 gap-4 mt-10">
            <div className="col-span-2">
              <label htmlFor="email">Email</label>
              <input
                type="text"
                placeholder="Enter your email"
                name="email"
                id="email"
                value={loginData.email !== null ? loginData.email : ""}
                onChange={handleChange}
                className="w-full border-2 border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-300 focus:border-transparent"
              />
            </div>
            <div className="col-span-2">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                placeholder="Enter your password"
                value={loginData.password}
                onChange={handleChange}
                name="password"
                id="password"
                className="w-full border-2 border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-300 focus:border-transparent"
              />
            </div>
            <div className="col-span-2">
              <button className="w-full bg-slate-500 text-white py-2 rounded-md">
                Login
              </button>

              {token && (
                <button
                  onClick={handleLogout}
                  className="w-full bg-slate-500 text-white py-2 rounded-md"
                >
                  Logout
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </form>
    </AutContext.Provider>
  );
}
