import React, { useState, useEffect } from "react";
import TopBar from "../TopBar";
import Mainbar from "../Mainbar";
import { Link } from "react-router-dom";

export default function AdminPanel() {


  return (
    <div>
      <div>
        <TopBar />
        <Mainbar />
      </div>
      <div className="flex flex-col">
        <input
          type="text"
          placeholder="name"
          value={recipeData.name}
          onChange={(e) =>
            setRecipeData({ ...recipeData, name: e.target.value })
          }
        />{" "}
        <input
          type="text"
          placeholder="description"
          value={recipeData.description}
          onChange={(e) =>
            setRecipeData({ ...recipeData, description: e.target.value })
          }
        />{" "}
        <input
          type="text"
          placeholder="ingredients"
          value={recipeData.ingredients.join(",")}
          onChange={handleIngredientsChange}
        />
        <input
          type="text"
          placeholder="prep time"
          value={recipeData.prep_time}
          onChange={(e) =>
            setRecipeData({ ...recipeData, prep_time: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="calories"
          value={recipeData.calories}
          onChange={(e) =>
            setRecipeData({ ...recipeData, calories: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="vegetarian"
          value={recipeData.vegetarian}
          onChange={(e) =>
            setRecipeData({ ...recipeData, vegetarian: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="diet"
          value={recipeData.diet}
          onChange={(e) =>
            setRecipeData({ ...recipeData, diet: e.target.value })
          }
        />{" "}
        <input
          type="text"
          placeholder="Image"
          value={recipeData.image}
          onChange={(e) =>
            setRecipeData({ ...recipeData, image: e.target.value })
          }
        />
        <select onChange={handleCategoryChange} onClick={fetchCatSub}>
          <option value="">Select Category</option>
          {filteredCategories.map((category) => (
            <option key={category._id} value={category._id}>
              {category.name}
            </option>
          ))}
        </select>
        {/* Select subcategory */}
        <select onChange={handleSubCategoryChange}>
          <option value="">Select Subcategory</option>
          {filteredSubCategories.map((subcategory) => (
            <option key={subcategory._id} value={subcategory._id}>
              {subcategory.name}
            </option>
          ))}
        </select>
        <button onClick={createRecipe}>Create Recipe</button>
      </div>
    </div>
  );
}

