import React from "react";
import TopBar from "../TopBar";
import Mainbar from "../Mainbar";
import { useState } from "react";
import { useEffect } from "react";
import { Link } from "react-router-dom";
export default function AdminPanel() {
  const [searchTerm, setSearchTerm] = useState("");
  const [categories, setCategories] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);

  //request
  const [recipeData, setRecipeData] = useState({
    name: '',
    description: '',
    image: null,
    ingredients: [],
    prep_time: 0,
    calories: 0,
    vegetarian: false,
    diet: '',
    category: '',
    subcategory: '',
  });

  // Function to fetch recipes based on the search term
  const fetchRecipes = async () => {
    try {
      const response = await fetch(
        `http://localhost:8080/categories?name=${searchTerm}`
      );
      const data = await response.json();
      console.log(data);
      if (data && data.documents && Array.isArray(data.documents)) {
        setCategories(data.documents); // Assuming 'documents' contains the array of recipes
      }
    } catch (error) {
      console.error("Error fetching recipes:", error);
    }
  };
  console.log(categories);

  // Update filtered recipes when the search term or recipes change
  useEffect(() => {
    setFilteredRecipes(
      categories.filter((recipe) =>
        recipe.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  }, [searchTerm, recipes]);

  // Handle input change
  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };



  console.log(filteredRecipes)

  return (
    <div>
      <div>
        <TopBar />
        <Mainbar />
      </div>
      <input
        type="text"
        placeholder="Search for recipes..."
        value={searchTerm}
        onChange={handleInputChange}
      />
      <button onClick={fetchRecipes}>Search</button>

      <ul>
              {filteredRecipes.map(recipe => (
                <li key={recipe._id}><Link to={`/recipe/${recipe._id}`}> {recipe.name} </Link></li>
                // Render other recipe details as needed
              ))}
            </ul>
    </div>
  );
}








