import { useLoaderData, Link, } from "react-router-dom";
import { useNavigate} from "react-router-dom";


export default function CategoriesList({ width, path }) {
  const data = useLoaderData();


console.log(data)
  const {
    categoriesList: { documents },
    recipesList: { documents: recipes },
  } = data || {};



  return (
    <div className={` p-0 pl-0lg:p-10 lg:pl-28 ${width} `}>
      <h1
        className="pb-4 font-montez text-4xl text-green-100 
        "
      >
        Filter recipes
      </h1>

      <h2 className="pt-4 text-xl text-green-200  ">Cusine</h2>
      {documents.map((el) => (
        <div
          key={el._id}
          className="text-md pl-2 font-light hover:text-green-200 hover:font-medium  w-100px"
        >
          <Link  to={`/${el._id}`.toString() }>{el.name}</Link>
        </div>
      ))}

      <h2 className="pt-4 text-xl text-green-200 ">Diet</h2>
      {recipes.map((el) => (
        el.diet === "true" && (

        <div
          key={el._id}
          className="text-md pl-2 font-light hover:text-green-200 hover:font-medium w-150px"
        >
          <Link to={`/recipe/${el._id}`}>{el.name}</Link>
        </div>
      )))}
      <h2 className="pt-4 text-xl text-green-200 ">vegetarians</h2>
      {recipes
        .map((el) => (
          el.vegetarian && (
          <div
            key={el._id}
            className="text-md pl-2 font-light hover:text-green-200 hover:font-medium w-200px "
          >
            <Link to={`/recipe/${el._id}`} className="break-words">{el.name}</Link>

          </div>
        )))}
    </div>
  );
}
