import { Link } from "react-router-dom";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faRightToBracket, faUser } from "@fortawesome/free-solid-svg-icons";
import { useContext } from "react";
import { TokenContext } from "../pages/TokenContext";

export default function TopBar() {
  const { token, setToken, handleLogout } = useContext(TokenContext);

  return (
    <div className="flex justify-between px-0 md:px-28 py-2 bg-black text-white shadow-xl">
      <div className="flex">
        <Link to="/" className="mr-8 text-sm md:text-base">
          Community
        </Link>
        <Link to="/" className="mr-8 text-sm md:text-base">
          Books
        </Link>
        <Link to="/" className="text-sm md:text-base">
          Popular
        </Link>
      </div>
      <div className="flex">
        {!token ? (
          <>
            <Link to="/login" className="mr-8 text-sm md:text-base ">
              <FontAwesomeIcon icon={faRightToBracket} className="mr-4 text-green-500" />
              Login
            </Link>
            <Link to="/signup" className="mr-2 md:mr-0 text-sm md:text-base ">
              <FontAwesomeIcon icon={faUser} className="mr-4 text-green-500" />
              Sign Up
            </Link>
          </>
        ) : (
          <button className="mr-2 md:mr-0 text-sm md:text-base" onClick={handleLogout}>
            <FontAwesomeIcon icon={faUser} className="mr-4 text-green-500" />
            Logout
          </button>
        )}
      </div>
    </div>
  );
}
