import React from "react";
import { useState } from "react";
import { useAuth } from "../pages/TokenContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGear } from "@fortawesome/free-solid-svg-icons";

export default function CreateCollection({updateCollections, coll}) {
  const [collectionName, setCollectionName] = useState("");
  const [collectionId, setcolletionId] = useState();
  const[showSettings, setShowSettings] = useState(false)
        const { token } = useAuth();

  const handleCollectionNameChange = (event) => {
    setCollectionName(event.target.value);
  };
  const fetchCollection = async () => {
        try {
                const response = await fetch("http://localhost:8080/collections", {
                method: "POST",
                headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({
                name: collectionName,
                }),
                });

                if (!response.ok) {
                throw new Error("Something went wrong");
                }

                const data = await response.json();

                updateCollections();
                setCollectionName("")
        }
        catch (error) {
                console.log(error);
        }
        }
        const handleCollectionsId= (event) => {
          setcolletionId(event.target.value)
        }

        const deleteCollection = async (id) => {
                try {
                        const response = await fetch(`http://localhost:8080/collections/${collectionId}`, {
                        method: "DELETE",
                        headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                        },
                        });

                        if (!response.ok) {
                        throw new Error("Something went wrong");
                        }

                        const data = await response.json();
                        updateCollections();
                        setCollectionName("")
                }
                catch (error) {
                        console.log(error);
                }
                }


  return (
    <div className="mb-4">
      <div>
        <FontAwesomeIcon icon={faGear} onClick={() => setShowSettings(!showSettings)} className="text-green-900 text-2xl point"/>
        {showSettings && (
          <div>
            <div className="mb-4 mt-4">
              <input
                type="text"
                placeholder="Collection name"
                value={collectionName}
                onChange={handleCollectionNameChange}
              />
              <button onClick={fetchCollection}>Create</button>
            </div>
            <div>
              <select>
                <option value="" key="">Select collection</option>
                {coll?.map((collection) => (
                  <option value={collection.id} onClick={handleCollectionsId}>{collection.name}</option>
                ))}
              </select>
              <button onClick={() => deleteCollection(collectionId)}>Delete</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
