import {
  Route,
  RouterProvider,
  createBrowserRouter,
  createRoutesFromElements,
  Navigate,
} from "react-router-dom";
import "./index.css";
import Home from "./pages/Home";
import Rootlayout from "./layouts/Rootlayout";
import Catdetails from "./components/reecipes/Catdetails";
import { catDetailsLoader } from "./components/reecipes/loader";
import { catLoader } from "./components/reecipes/loader";
import RecipesDetail from "./components/reecipes/RecipesDetail";

import { recipeDetailsLoader } from "./components/reecipes/loader";
import Loginpage from "./pages/Loginpage";
import AuthProvider from 'react-auth-kit/AuthProvider';
import { createContext } from "react";


export const TokenContext = createContext();

const router = createBrowserRouter(
  createRoutesFromElements(
    <TokenContext.Provider value={{ token , setToken}}>
    <Route path="/" element={<Rootlayout />}>
      <Route index element={<Home />} loader={catLoader} />
      <Route path="/login" element={<Loginpage/>}  />

      <Route path=":id" element={<Catdetails />} loader={catDetailsLoader} />

      <Route path="recipe/:id" element={<RecipesDetail />} loader={recipeDetailsLoader}>

      </Route>
      <Route path="recipe/:id/:id" element={<Catdetails />} loader={catDetailsLoader} />

    </Route>
    </TokenContext.Provider>
  )
);

function App() {

  return( 
    <AuthProvider authStorageType={'cookie'} authName={"_auth"} cookieDomain={window.location.hostname} cookieSecure={false} store= {"coockie"}>
  <RouterProvider router={router} />
  </AuthProvider>
  )
}

export default App;

