import CategoriesList from "./CategoriesList";
import RecipesNav from "./RecipesNav";
import mainfruite from "../../assets/images/mainfruite.png";
import { useLoaderData } from "react-router-dom";
import { useEffect } from "react";
import { useState } from "react";
import { catDetailsLoader, recipeDetailsLoader } from "./loader";

export default function RecipesDetail() {

  const [recipeDetailsLoader, catLoader] = useLoaderData();
  const [recipeDetails, setRecipeDetails] = useState();
  useEffect(() => {
  const handleRecipeDetails = async () => {
    try {
      // Call the async function to fetch recipe details
      const result = await recipeDetailsLoader();
      // 'result' contains the data fetched from recipeDetailsLoader
      setRecipeDetails(result);
      // Use the fetched data as needed
    } catch (error) {
      // Handle any errors that might occur in the process
      console.error("Error handling recipe details:", error);
    }
  };
  }, [recipeDetailsLoader]);

  console.log(recipeDetails)

  return (
    <div className="flex w-full relative">
      {/* <CategoriesList width={"w-1/4 "} /> */}
      <div className=" w-2/3 ">
        <RecipesNav />
        <div>

        </div>
      </div>
      <img src={mainfruite} alt="" className="w-1/4 h-1000px bg-" />
    </div>
  );
}
