import { useLoaderData, Link } from "react-router-dom";
import { useState } from "react";
import Pagination from "../Pagination";
import logo from "../../assets/images/s1.jpg";

export default function DrinksCard({
  currentPage,
  setCurrentPage,
  postsPerPage,
  indexOfFirstPost,
  indexOfLastPost,
  len
  }) {
  const data = useLoaderData();

  const {
    recipesList: { documents },
  } = data || {};

  const filterRecipes = documents.filter((el) =>
    el.category.includes("656131c155fa025551457e36")
  );

  const currentPosts = filterRecipes.slice(indexOfFirstPost, indexOfLastPost);


  return (
    <div className="">
      <h1 className="font-montez text-4xl text-green-300 mt-2">Drinks</h1>
      <div className="grid grid-cols-3 h-150px gap-8 bg-slate-400 ">
        {currentPosts.map((el) => (
          <div key={el._id} className="">
            <Link className="">
              <img src={el.image} alt=""className="w-250px h-250px" />
            </Link>
            <div className="pl-4">
              <h1>{el.name}</h1>
              {/* <p>{el.description}</p> */}
              <Link to={`details/${el._id}`.toString()}>Read more</Link>

            </div>
          </div>
        ))}
      </div>
      <Pagination
        totalRes={len}
        postsPerPage={postsPerPage}
        setCurrentPage={setCurrentPage}
        isHidden={"hidden"}
      />
    </div>
  );
}
