import { useLoaderData, Link } from "react-router-dom";
import Pagination from "../Pagination";

src/components/reecipes/BakeryCard.js
export default function BakeryCard ({
  currentPage,
  setCurrentPage,
  postsPerPage,
  indexOfFirstPost,
  indexOfLastPost,
  len
  }) {
  const data = useLoaderData();

  const {
    recipesList: { documents },
  } = data || {};

  const filterRecipes = documents.filter((el) =>
    el.category.includes("65678b30e66dda14da9127c5")
  );

  const currentPosts = filterRecipes.slice(indexOfFirstPost, indexOfLastPost);

  console.log(documents);
  return (
    <div className="">
      <h1 className="font-montez text-4xl text-green-300 mt-2">Bakery</h1>
      <div className="grid grid-cols-3 h-300px gap-8">
        {currentPosts.map((el) => (
          <div key={el._id} className="">
            <div>
              <img src={el.image} alt="" className="w-250px h-250px " />
            </div>
            <div className="pl-4">
              <h1>{el.name}</h1>
              {/* <p>{el.description}</p> */}
              <Link to={`details/${el._id}`.toString()}>Read more</Link>
              <image src={el.image} alt="" />
            </div>
          </div>
        ))}
      </div>
      <Pagination
        totalRes={len}
        postsPerPage={postsPerPage}
        setCurrentPage={setCurrentPage}
        styles="absolute left-72 top-0 flex flex-col space-y-2"

      />
    </div>
  );
}
